// src/pages/About.js
import React from 'react';

function About() {
  const [posts, setPosts] = useState([]);
  const [error, setError] = useState(null);

  useEffect(() => {
    const fetchPosts = async () => {
      try {
        const fetchedPosts = await getPosts();
        setPosts(fetchedPosts);
      } catch (err) {
        console.error('Error fetching posts:', err);
        setError(err.message || 'An error occurred while fetching posts');
      }
    };

    fetchPosts();
  }, []);

  if (error) {
    return <div>Error: {error}</div>;
  }
  return (
    <div className="about">
      <h1>About Me</h1>
      <p>Welcome to my blog! I'm a passionate writer and developer sharing my thoughts and experiences.</p>
      <p>Feel free to explore my posts and get in touch if you have any questions or just want to chat.</p>
    </div>
  );
}

export default About;
