import React, { useState, useEffect } from 'react';
import { Link } from 'react-router-dom';
import { getUserProfile, getPosts } from '../services/api';
import { useAuth } from '../contexts/AuthContext';

function UserDashboard() {
  const [userInfo, setUserInfo] = useState(null);
  const [userPosts, setUserPosts] = useState([]);
  const [error, setError] = useState('');
  const { user } = useAuth();

  useEffect(() => {
    const fetchUserData = async () => {
      try {
        const info = await getUserProfile(user.userId);
        setUserInfo(info);
        const posts = await getPosts(); // Note: This might need to be filtered for user-specific posts
        setUserPosts(posts.filter(post => post.userId === user.userId)); // Client-side filtering
      } catch (err) {
        setError('Failed to fetch user data');
        console.error(err);
      }
    };

    if (user) {
      fetchUserData();
    }
  }, [user]);

  if (!user) {
    return <div>Please log in to view your dashboard.</div>;
  }

  if (error) {
    return <div className="error">{error}</div>;
  }

  return (
    <div className="user-dashboard">
      <h1>Welcome to Your Dashboard</h1>
      {userInfo && (
        <div className="user-info">
          <h2>Your Information</h2>
          <p>Name: {userInfo.name}</p>
          <p>Email: {userInfo.email}</p>
          {/* Add more user info fields as needed */}
        </div>
      )}
      <div className="user-posts">
        <h2>Your Posts</h2>
        {userPosts.length === 0 ? (
          <p>You haven't created any posts yet.</p>
        ) : (
          <ul>
            {userPosts.map(post => (
              <li key={post._id}>
                <Link to={`/post/${post._id}`}>{post.title}</Link>
                <Link to={`/edit-post/${post._id}`}>Edit</Link>
              </li>
            ))}
          </ul>
        )}
      </div>
      <Link to="/create-post">Create New Post</Link>
    </div>
  );
}

export default UserDashboard;
